            Assert.Contains("Error while writing to 'MyTarget([unnamed])'. Try 3/4", result);
            Assert.Contains("Error while writing to 'MyTarget([unnamed])'. Try 4/4", result);
            Assert.Contains("Too many retries. Aborting.", result);
