{
			MethodResult result = RunClass(typeof(ClassDataTestClass)).Single();

			Assert.IsType<PassedResult>(result);
			Assert.Equal(@"Xunit1.Extensions.ClassDataAcceptanceTests+ClassDataTestClass.PassingTestData(foo: 1, bar: ""hello world"", baz: 2.3)", result.DisplayName);
		}

{
			MethodResult[] results = RunClass(typeof(MultipleDataSetsClass)).ToArray();

			Assert.Equal(2, results.Length);
			PassedResult passedResult = results.OfType<PassedResult>().Single();
			Assert.Equal(@"Xunit1.Extensions.InlineDataAcceptanceTests+MultipleDataSetsClass.PassingTestData(foo: 1, bar: ""hello"", baz: 2.3)", passedResult.DisplayName);
			FailedResult failedResult = results.OfType<FailedResult>().Single();
			Assert.Equal(@"Xunit1.Extensions.InlineDataAcceptanceTests+MultipleDataSetsClass.PassingTestData(foo: 42, bar: ""world"", baz: 21.12)", failedResult.DisplayName);
		}

{
			MethodResult result = RunClass(typeof(NullValueClass)).Single();

			Assert.IsType<PassedResult>(result);
			Assert.Equal(@"Xunit1.Extensions.InlineDataAcceptanceTests+NullValueClass.PassingTestData(foo: null)", result.DisplayName);
		}

{
			MethodResult result = RunClass(typeof(PropertyDataTestClass)).Single();

			Assert.IsType<PassedResult>(result);
			Assert.Equal(@"Xunit1.Extensions.PropertyDataAcceptanceTests+PropertyDataTestClass.PassingTestData(foo: 1, bar: ""hello world"", baz: 2.3)", result.DisplayName);
		}

{
			MethodResult[] results = RunClass(typeof(IncorrectParameterCountClass)).ToArray();

			Assert.Equal(2, results.Length);
			PassedResult passedResult = results.OfType<PassedResult>().Single();
			Assert.Equal(@"Xunit1.Extensions.TheoryAcceptanceTests+IncorrectParameterCountClass.PassingTestData(x: 1)", passedResult.DisplayName);
			FailedResult failedResult = results.OfType<FailedResult>().Single();
			Assert.Equal(@"Xunit1.Extensions.TheoryAcceptanceTests+IncorrectParameterCountClass.PassingTestData(x: 2, ???: 3)", failedResult.DisplayName);
		}

{
			MethodResult result = RunClass(typeof(NoDataAttributesClass)).Single();

			FailedResult failedResult = Assert.IsType<FailedResult>(result);
			Assert.Equal(@"Xunit1.Extensions.TheoryAcceptanceTests+NoDataAttributesClass.TheoryMethod", failedResult.DisplayName);
			Assert.Equal("System.InvalidOperationException : No data found for Xunit1.Extensions.TheoryAcceptanceTests+NoDataAttributesClass.TheoryMethod", failedResult.Message);
		}

{
			MethodResult result = RunClass(typeof(EmptyTheoryDataClass)).Single();

			FailedResult failedResult = Assert.IsType<FailedResult>(result);
			Assert.Equal(@"Xunit1.Extensions.TheoryAcceptanceTests+EmptyTheoryDataClass.TheoryMethod", failedResult.DisplayName);
			Assert.Equal("System.InvalidOperationException : No data found for Xunit1.Extensions.TheoryAcceptanceTests+EmptyTheoryDataClass.TheoryMethod", failedResult.Message);
		}

{
			MethodResult result = RunClass(typeof(ThrowingDataClass)).Single();

			FailedResult failedResult = Assert.IsType<FailedResult>(result);
			Assert.Equal(@"Xunit1.Extensions.TheoryAcceptanceTests+ThrowingDataClass.TheoryWithMisbehavingData", failedResult.DisplayName);
			Assert.Contains("System.InvalidOperationException : An exception was thrown while getting data for theory Xunit1.Extensions.TheoryAcceptanceTests+ThrowingDataClass.TheoryWithMisbehavingData", failedResult.Message);
		}

{
			DataAdapterDataAttribute attr = new TestableDataAdapterDataAttribute(DBNull.Value);

			List<object[]> results = new List<object[]>(attr.GetData(null, new Type[] { typeof(object) }));

			object[] result = Assert.Single(results);
			object singleResult = Assert.Single(result);
			Assert.Null(singleResult);
		}

{
			DataAdapterDataAttribute attr = new TestableDataAdapterDataAttribute(DBNull.Value);

			Assert.DoesNotThrow(() => new List<object[]>(attr.GetData(null, new Type[0])));
		}

{
			MethodInfo method = typeof(TestMethodCommandClass).GetMethod("TestViaProperty");
			TheoryAttribute attr = (TheoryAttribute)(method.GetCustomAttributes(typeof(TheoryAttribute), false))[0];

			List<ITestCommand> commands = new List<ITestCommand>(attr.CreateTestCommands(Reflector.Wrap(method)));

			ITestCommand command = Assert.Single(commands);
			TheoryCommand theoryCommand = Assert.IsType<TheoryCommand>(command);
			object parameter = Assert.Single(theoryCommand.Parameters);
			Assert.Equal(2, parameter);
		}

{
			MethodInfo method = typeof(TestMethodCommandClass).GetMethod("TestViaPropertyOnBaseClass");
			TheoryAttribute attr = (TheoryAttribute)(method.GetCustomAttributes(typeof(TheoryAttribute), false))[0];

			List<ITestCommand> commands = new List<ITestCommand>(attr.CreateTestCommands(Reflector.Wrap(method)));

			ITestCommand command = Assert.Single(commands);
			TheoryCommand theoryCommand = Assert.IsType<TheoryCommand>(command);
			object parameter = Assert.Single(theoryCommand.Parameters);
			Assert.Equal(4, parameter);
		}

{
			MethodInfo method = typeof(TestMethodCommandClass).GetMethod("TestViaOtherTypeProperty");
			TheoryAttribute attr = (TheoryAttribute)(method.GetCustomAttributes(typeof(TheoryAttribute), false))[0];

			List<ITestCommand> commands = new List<ITestCommand>(attr.CreateTestCommands(Reflector.Wrap(method)));

			ITestCommand command = Assert.Single(commands);
			TheoryCommand theoryCommand = Assert.IsType<TheoryCommand>(command);
			object parameter = Assert.Single(theoryCommand.Parameters);
			Assert.Equal(3, parameter);
		}

{
			MethodInfo method = typeof(TestMethodCommandClass).GetMethod("GenericTest");
			TheoryAttribute attr = (TheoryAttribute)(method.GetCustomAttributes(typeof(TheoryAttribute), false))[0];

			List<ITestCommand> commands = new List<ITestCommand>(attr.CreateTestCommands(Reflector.Wrap(method)));

			Assert.Equal(4, commands.Count);
			TheoryCommand command1 = Assert.IsType<TheoryCommand>(commands[0]);
			Assert.Equal(@"Xunit1.Extensions.TheoryAttributeTests+TestMethodCommandClass.GenericTest<Int32>(value: 42)", command1.DisplayName);
			TheoryCommand command2 = Assert.IsType<TheoryCommand>(commands[1]);
			Assert.Equal(@"Xunit1.Extensions.TheoryAttributeTests+TestMethodCommandClass.GenericTest<String>(value: ""Hello, world!"")", command2.DisplayName);
			TheoryCommand command3 = Assert.IsType<TheoryCommand>(commands[2]);
			Assert.Equal(@"Xunit1.Extensions.TheoryAttributeTests+TestMethodCommandClass.GenericTest<Int32[]>(value: System.Int32[])", command3.DisplayName);
			// TODO: Would like to see @"TheoryAttributeTests+TestMethodCommandClass.GenericTest<Int32[]>(value: Int32[] { 1, 2, 3 })"
			TheoryCommand command4 = Assert.IsType<TheoryCommand>(commands[3]);
			Assert.Equal(@"Xunit1.Extensions.TheoryAttributeTests+TestMethodCommandClass.GenericTest<List<String>>(value: System.Collections.Generic.List`1[System.String])", command4.DisplayName);
			// TODO: Would like to see @"TheoryAttributeTests+TestMethodCommandClass.GenericTest<List<String>>(value: List<String> { ""a"", ""b"", ""c"" })"
		}

{
			MethodInfo methodInfo = typeof(InstrumentedSpy).GetMethod("PassedTest");
			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), null);
			InstrumentedSpy.ctorCounter = 0;
			InstrumentedSpy.passedTestCounter = 0;

			command.Execute(new InstrumentedSpy());

			Assert.Equal(1, InstrumentedSpy.ctorCounter);
			Assert.Equal(1, InstrumentedSpy.passedTestCounter);
		}

{
			MethodInfo methodInfo = typeof(DisposableSpy).GetMethod("PassedTest");
			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), null);
			DisposableSpy.ctorCalled = 0;
			DisposableSpy.disposeCalled = 0;

			ITestResult result = command.Execute(new DisposableSpy());

			Assert.IsType<PassedResult>(result);
		}

{
			TheoryCommand command = new TheoryCommand(
				Reflector.Wrap(typeof(ParameterSpy).GetMethod("Method")),
				new object[] { 2 });

			Assert.Throws<InvalidOperationException>(() => command.Execute(new ParameterSpy()));
		}

{
			MethodInfo methodInfo = typeof(DummyWithAttributes).GetMethod("TheoryMethod");

			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), new object[] { 42, 24.5 });

			Assert.Equal("My display name(x: 42, y: 24.5)", command.DisplayName);
		}

{
			MethodInfo methodInfo = typeof(DummyWithAttributes).GetMethod("TheoryMethod");

			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), new object[] { 42, 24.5, "Hello!", 'c' });

			Assert.Equal("My display name(x: 42, y: 24.5, ???: \"Hello!\", ???: 'c')", command.DisplayName);
		}

{
			MethodInfo methodInfo = typeof(DummyWithAttributes).GetMethod("TheoryMethod");

			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), new object[] { 42 });

			Assert.Equal("My display name(x: 42, y: ???)", command.DisplayName);
		}

{
			MethodInfo methodInfo = typeof(SpyWithDataPassed).GetMethod("Test");
			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), new object[] { 42, 24.5, "foo" });
			SpyWithDataPassed.X = 0;
			SpyWithDataPassed.Y = 0.0;
			SpyWithDataPassed.Z = null;

			command.Execute(new SpyWithDataPassed());

			Assert.Equal(42, SpyWithDataPassed.X);
			Assert.Equal(24.5, SpyWithDataPassed.Y);
			Assert.Equal("foo", SpyWithDataPassed.Z);
		}

{
			MethodInfo methodInfo = typeof(TestMethodCommandClass).GetMethod("TestMethod");
			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), null);

			MethodResult result = command.Execute(new TestMethodCommandClass());

			Assert.IsType<PassedResult>(result);
		}

{
			MethodInfo methodInfo = typeof(TestMethodCommandClass).GetMethod("ThrowsException");
			TheoryCommand command = new TheoryCommand(Reflector.Wrap(methodInfo), null);

			Assert.Throws<InvalidOperationException>(() => command.Execute(new TestMethodCommandClass()));
		}

{
			TheoryCommand command = new TheoryCommand(
				Reflector.Wrap(typeof(ParameterSpy).GetMethod("Method")),
				new object[] { 2, "foo", 3.14 });

			Assert.Throws<InvalidOperationException>(() => command.Execute(new ParameterSpy()));
		}

{
			StringBuilder sb = new StringBuilder(500);

			for (int idx = 0; idx < 50; idx++)
				sb.Append("----=----|");

			TheoryCommand command = new TheoryCommand(
				Reflector.Wrap(typeof(ParameterSpy).GetMethod("Method")),
				new object[] { 2, sb.ToString() });

			MethodResult result = command.Execute(new ParameterSpy());

			Assert.IsType<PassedResult>(result);
			Assert.Equal(@"Xunit1.Extensions.TheoryCommandTests+ParameterSpy.Method(x: 2, y: ""----=----|----=----|----=----|----=----|----=----|""...)", result.DisplayName);
		}

{
			TheoryCommand command = new TheoryCommand(Reflector.Wrap(typeof(DummyWithAttributes).GetMethod("TimeoutMethod")), null);

			Assert.Equal(153, command.Timeout);
		}

{
			string expectedXml = @"<start name=""Xunit1.Extensions.TheoryCommandTests+DummyWithAttributes.StringMethod(s: &quot;\x0\xFFFF&quot;)"" type=""Xunit1.Extensions.TheoryCommandTests+DummyWithAttributes"" method=""StringMethod"" />";

			TheoryCommand command = new TheoryCommand(Reflector.Wrap(typeof(DummyWithAttributes).GetMethod("StringMethod")), new object[] { "\0\xffff" });

			Assert.Equal(expectedXml, command.ToStartXml().OuterXml);
		}

{
			DateTime reference = DateTime.Now;

			DateTime result1 = Clock.Now;
			Thread.Sleep(100);
			DateTime result2 = Clock.Now;

			Assert.Equal(result1, result2);
			Assert.True((reference - result1).TotalMilliseconds < 1000);
		}

{
			DateTime result = Clock.Now;

			Assert.Equal(new DateTime(2006, 12, 31), result);
		}

{
			DateTime result = Clock.Now;

			Assert.Equal(new DateTime(2006, 12, 31, 4, 5, 6), result);
		}

{
			DateTime result = Clock.Now;

			Assert.Equal(new DateTime(2006, 12, 31, 4, 5, 6, DateTimeKind.Utc).ToLocalTime(), result);
		}

{
			DateTime result1 = Clock.Now;
			Thread.Sleep(100);
			DateTime result2 = Clock.Now;

			Assert.NotEqual(result1, result2);
		}

{
			Assert.Equal(ApartmentState.STA, Thread.CurrentThread.GetApartmentState());
		}

{
			IEnumerable<MethodResult> results = RunClass(typeof(Async40AcceptanceTestClass));

			MethodResult result = Assert.Single(results);
			FailedResult failedResult = Assert.IsType<FailedResult>(result);
			Assert.Equal(typeof(TrueException).FullName, failedResult.ExceptionType);
		}

{
				return Task.Factory.StartNew(() =>
				{
					Thread.Sleep(1);
				})
				.ContinueWith(_ =>
				{
					Assert.True(false);
				});
			}

{
			MethodResult result = RunClass(typeof(ClassUnderTest)).Single();

			FailedResult failedResult = Assert.IsType<FailedResult>(result);
			Assert.Equal(typeof(InvalidOperationException).FullName, failedResult.ExceptionType);
			Assert.Equal("System.InvalidOperationException : Fact method Xunit1.FactMethodWithArgumentsTests+ClassUnderTest.FactWithParameters cannot have parameters", failedResult.Message);
		}

{
			MethodResult result = RunClass(typeof(ClassUnderTest)).Single();

			Assert.IsType<FailedResult>(result);
			Assert.NotEqual(0.0, result.ExecutionTime);
		}

{
				Thread.Sleep(100);
				Assert.Equal(2, 3);
			}

{
			MethodResult result = RunClass(typeof(FixtureWithAllSkips)).Single();

			// If it ran the fixture, then we would get a class failure
			Assert.IsType<SkipResult>(result);
		}

{
			MethodResult result = RunClass(typeof(FixtureWithAllStatics)).Single();

			// If it ran the fixture, then we would get a class failure
			Assert.IsType<PassedResult>(result);
		}

{
			MethodResult result = RunClass(typeof(ClassUnderTest)).Single();

			// If we ran the constructor, we would get a failure instead of a skip.
			Assert.IsType<SkipResult>(result);
		}

{
			MethodResult result = RunClass(typeof(ClassUnderTest)).Single();

			FailedResult failedResult = Assert.IsType<FailedResult>(result);
			Assert.InRange(failedResult.ExecutionTime, 0.049, 0.051);
			Assert.Equal("Test execution time exceeded: 50ms", failedResult.Message);
		}

{
				Thread.Sleep(120);
				Assert.Equal(2, 2);
			}

{
				List<object> list = new List<object> { 16, null, "Hi there" };

				Assert.Contains(null, list);
			}

{
				List<int> list = new List<int> { 42 };

				Assert.Contains(43, list, new MyComparer());
			}

{
				List<int> list = new List<int> { 42 };

				Assert.Contains(42, list);
			}

{
				List<int> list = new List<int>();

				ContainsException ex = Assert.Throws<ContainsException>(() => Assert.Contains(42, list));

				Assert.Equal("Assert.Contains() failure: Not found: 42", ex.Message);
			}

{
				List<object> list = new List<object> { null, 16, "Hi there" };

				Assert.Contains("Hi there", list);
			}

{
				Assert.Throws<ContainsException>(() => Assert.Contains(14, (List<int>)null));
			}

{
				Assert.Contains("wor", "Hello, world!");
			}

{
				Assert.Contains("WORLD", "Hello, world!", StringComparison.InvariantCultureIgnoreCase);
			}

{
				var ex = Record.Exception(() => Assert.Contains("WORLD", "Hello, world!"));

				Assert.IsType<ContainsException>(ex);
				Assert.Equal(
					"Assert.Contains() failure:" + Environment.NewLine +
					"Not found: WORLD" + Environment.NewLine +
					"In value:  Hello, world!", ex.Message);
			}

{
				Assert.Throws<ContainsException>(() => Assert.Contains("hey", "Hello, world!"));
			}

{
				Assert.Throws<ContainsException>(() => Assert.Contains("foo", (string)null));
			}

{
				List<object> list = new List<object> { 16, "Hi there" };

				Assert.DoesNotContain(null, list);
			}

{
				List<int> list = new List<int>();
				list.Add(42);

				Assert.DoesNotContain(42, list, new MyComparer());
			}

{
				List<int> list = new List<int> { 42 };

				DoesNotContainException ex =
					Assert.Throws<DoesNotContainException>(() => Assert.DoesNotContain(42, list));

				Assert.Equal("Assert.DoesNotContain() failure: Found: 42", ex.Message);
			}

{
				List<int> list = new List<int>();

				Assert.DoesNotContain(42, list);
			}

{
				List<object> list = new List<object> { null, 16, "Hi there" };

				Assert.DoesNotContain(42, list);
			}

{
				Assert.DoesNotThrow(() => Assert.DoesNotContain(14, (List<int>)null));
			}

{
				Assert.DoesNotContain("hey", "Hello, world!");
			}

{
				Assert.Throws<DoesNotContainException>(
					() => Assert.DoesNotContain("WORLD", "Hello, world!", StringComparison.InvariantCultureIgnoreCase));
			}

{
				Assert.DoesNotContain("WORLD", "Hello, world!");
			}

{
				Assert.Throws<DoesNotContainException>(() => Assert.DoesNotContain("world", "Hello, world!"));
			}

{
				Assert.DoesNotThrow(() => Assert.DoesNotContain("foo", (string)null));
			}

{
			DoesNotThrowException ex =
				Assert.Throws<DoesNotThrowException>(
					() => Assert.DoesNotThrow(
						() => { throw new NotImplementedException("Exception Message"); }));

			Assert.Equal("Assert.DoesNotThrow() failure", ex.UserMessage);
			Assert.Equal("(No exception)", ex.Expected);
			Assert.Equal("System.NotImplementedException: Exception Message", ex.Actual);
		}

{
			Assert.DoesNotThrow(() => { });
		}

{
				List<int> list = new List<int>();

				Assert.Empty(list);
			}

{
				List<int> list = new List<int>();
				list.Add(42);

				EmptyException ex = Assert.Throws<EmptyException>(() => Assert.Empty(list));

				Assert.Equal("Assert.Empty() failure", ex.Message);
			}

{
				Assert.Throws<ArgumentNullException>(() => Assert.Empty(null));
			}

{
				Assert.Empty("");
			}

{
				EmptyException ex = Assert.Throws<EmptyException>(() => Assert.Empty("Foo"));

				Assert.Equal("Assert.Empty() failure", ex.Message);
			}

{
				string[] expected = { "@", "a", "ab", "b" };
				string[] actual = { "@", "a", "ab", "b" };

				Assert.Equal(expected, actual);
				Assert.Throws<NotEqualException>(() => Assert.NotEqual(expected, actual));
			}

{
				string[][] expected = { new[] { "@", "a" }, new[] { "ab", "b" } };
				string[][] actual = { new[] { "@", "a" }, new[] { "ab", "b" } };

				Assert.Equal(expected, actual);
				Assert.Throws<NotEqualException>(() => Assert.NotEqual(expected, actual));
			}

{
				string[] expected = { "@", "a", "ab", "b", "c" };
				string[] actual = { "@", "a", "ab", "b" };

				Assert.Throws<EqualException>(() => Assert.Equal(expected, actual));
				Assert.NotEqual(expected, actual);
			}

{
				string[] expected = { "@", "d", "v", "d" };
				string[] actual = { "@", "a", "ab", "b" };

				Assert.Throws<EqualException>(() => Assert.Equal(expected, actual));
				Assert.NotEqual(expected, actual);
			}

{
				int[] expected = new[] { 1, 2, 3, 4, 5 };
				List<int> actual = new List<int>(expected);

				Assert.Equal(expected, actual);
			}

{
				int[] expected = new[] { 1, 2, 3, 4, 5 };
				List<int> actual = new List<int>(new[] { 1, 2, 3, 4, 6 });

				EqualException ex = Assert.Throws<EqualException>(() => Assert.Equal(expected, actual));

				Assert.Contains("First difference is at position 4" + Environment.NewLine, ex.Message);
			}

{
				int[] expected = new[] { 1, 2, 3, 4, 5 };
				List<int> actual = new List<int>(new int[] { 0, 0, 0, 0, 0 });

				Assert.Equal(expected, actual, new IntComparer(true));
			}

{
				int[] expected = new[] { 1, 2, 3, 4, 5 };
				List<int> actual = new List<int>(new int[] { 1, 2, 3, 4, 5 });

				EqualException ex = Assert.Throws<EqualException>(() => Assert.Equal(expected, actual, new IntComparer(false)));

				// TODO: When we fix up the assert exception messages, we should allow the enumerator who
				// did the comparisons to tell us exactly where the error was, rather than determining the
				// inequivalence after the fact.

				// Assert.Contains("First difference is at position 0\r\n", ex.Message);
			}

{
				var x = new List<object> { new List<object> { new List<object> { new List<object>() } } };
				var y = new List<object> { new List<object> { new List<object> { new List<object>() } } };

				Assert.Equal(x, y);
			}

{
				ComparableObject obj1 = new ComparableObject();
				ComparableObject obj2 = new ComparableObject();

				Assert.Equal(obj1, obj2);
				Assert.True(obj1.CompareCalled);
			}

{
				GenericComparableObject obj1 = new GenericComparableObject();
				GenericComparableObject obj2 = new GenericComparableObject();

				Assert.Equal(obj1, obj2);
				Assert.True(obj1.CompareCalled);
			}

{
				NonComparableObject nco1 = new NonComparableObject();
				NonComparableObject nco2 = new NonComparableObject();

				Assert.Equal(nco1, nco2);
			}

{
				Assert.Equal(double.NegativeInfinity, double.NegativeInfinity);
			}

{
				Assert.Throws<EqualException>(() => Assert.Equal(1.23, double.NegativeInfinity));
			}

{
				Assert.Equal(double.PositiveInfinity, double.PositiveInfinity);
			}

{
				Assert.Throws<EqualException>(() => Assert.Equal(1.23, double.PositiveInfinity));
			}

{
				Assert.Throws<EqualException>(() => Assert.Equal(double.NegativeInfinity, double.PositiveInfinity));
			}

{
				IEnumerable<int> items = IntGenerator.Range(1, 12);
				IEnumerable<int> others = IntGenerator.Range(1, 12);

				Assert.Equal(items, others);
			}

{
				EquatableObject obj1 = new EquatableObject();
				EquatableObject obj2 = new EquatableObject();

				Assert.Equal(obj1, obj2);

				Assert.True(obj1.Equals__Called);
				Assert.Same(obj2, obj1.Equals_Other);
			}

{
				Assert.Throws<EqualException>(() => Assert.Equal(double.NaN, 1.234));
			}

{
				Assert.Throws<EqualException>(() => Assert.Equal(1.234, double.NaN));
			}

{
				Assert.Equal(double.NaN, double.NaN);
			}

{
				Assert.Equal<object>(null, null);
			}

{
				Assert.Throws<EqualException>(() => Assert.Equal(new object(), null));
			}

{
				Assert.Throws<EqualException>(() => Assert.Equal(null, new object()));
			}

{
				decimal expected = 25;
				decimal actual = 42;

				Assert.Throws<EqualException>(() => Assert.Equal(expected, actual));
			}

{
				double expected = 25.3;
				double actual = 42.0;

				Assert.Throws<EqualException>(() => Assert.Equal(expected, actual));
			}

{
				byte valueType = 35;
				Byte referenceValue = 35;

				Assert.True(valueType == referenceValue);
				Assert.Equal(referenceValue, valueType);
				Assert.Equal<byte>(valueType, 35);
				Assert.Equal<byte>(referenceValue, 35);
			}

{
				decimal valueType = 35;
				Decimal referenceValue = 35;

				Assert.True(valueType == referenceValue);
				Assert.Equal(referenceValue, valueType);
				Assert.Equal<decimal>(valueType, 35);
				Assert.Equal(valueType, 35M);
				Assert.Equal<decimal>(referenceValue, 35);
			}

{
				short valueType = 35;
				Int16 referenceValue = 35;

				Assert.True(valueType == referenceValue);
				Assert.Equal(referenceValue, valueType);
				Assert.Equal<short>(valueType, 35);
				Assert.Equal<short>(referenceValue, 35);
			}

{
				int valueType = 35;
				Int32 referenceValue = 35;

				Assert.True(valueType == referenceValue);
				Assert.Equal(referenceValue, valueType);
				Assert.Equal(valueType, 35);
				Assert.Equal(referenceValue, 35);
			}

{
				long valueType = 35;
